import { DcxNgBreadcrumbComponent, } from '@dcx-ng-components/dcx-ng-lib';
const meta = {
    title: 'DCXLibrary/Breadcrumb/Class based',
    component: DcxNgBreadcrumbComponent,
    tags: ['autodocs'],
    argTypes: {
        items: { control: { type: 'object' } },
    },
    args: {
        items: [
            { label: 'Inicio', href: '/' },
            { label: 'Productos', href: '/productos' },
            { label: 'Teclados' },
        ],
    },
};
export default meta;
export const ClassBasedDemo = {
    render: args => {
        const pathText = (args.items ?? [])
            .map((i) => i.label)
            .join(' / ');
        return {
            props: {
                ...args,
                pathText,
            },
            template: `
        <div style="display:grid; gap:8px; max-width:640px;">
          <dcx-ng-breadcrumb [items]="items"></dcx-ng-breadcrumb>
        </div>
      `,
        };
    },
};
